mapper.update:
UPDATE learning_data
SET study_time = #{studyTime}, updated_at = CURRENT_TIMESTAMP
WHERE user_id = #{userId} AND id = #{entryId}

mapper.select:
SELECT COUNT(*)
FROM learning_data
WHERE name = #{studyName} AND user_id = #{userId}

